        ... on OasisUnfreezeNodeEvent {
          type
          id
        }
        ... on OasisRegisterRuntimeEvent {
          type
          id
          version
        }
        ... on OasisRateEvent {
          type
          start
          rate
        }
        ... on OasisBoundEvent {
          type
          start
          rate_min
          rate_max
        }
        ... on OasisAmendCommissionScheduleEvent {
          type
          rates
          bounds
        }
        ... on OasisUnknownEvent {
          type
          method_name
        }
      }
    }
    moreResultsExist
  }
}

