      person: { id: $id, name: $name, note: $note, primaryLocation: $primaryLocation, user: $user, images: $images }
    ) {
      agent {
        id
        name
        note
        images {
          name
        }
        primaryLocation {
          id
          lat
          long
          name
        }
      }
    }
  }

{
    economicResource(id: $id) {
      trace {
        __typename
        ... on EconomicEvent {
          hasPointInTime
          action {
            id
          }
        }
      }
    }
  }

{
    people(last: $last, filter: $filter) {
      edges {
        node {
          id
          name
          user
          note
          images {
            bin
            mimeType
          }
          primaryLocation {
            id
            name
          }
        }
      }
    }
  }

{
    economicResources(
      last: $last
      filter: { id: $IDs, name: $name, conformsTo: $conformsTo, primaryAccountable: $primaryAccountable }
    ) {
      edges {
        node {
          id
          name
          metadata
          conformsTo {
            id
            name
          }
          primaryAccountable {
            id
            name
          }
          images {
            hash
            name
            mimeType
            bin
          }
        }
      }
    }
  }

{
    economicResource(id: $id) {
      id
      name
      images {
        bin
        mimeType
      }
      conformsTo {
        name
        id
      }
      primaryAccountable {
        name
      }
    }
  }

{
    economicResourceClassifications(filter: { uri: $text })
  }

{
    economicResource(id: $id) {
      trace {
        __typename
        ... on EconomicEvent {
          hasPointInTime
        }
      }
    }
  }

{
    economicResources(last: 10, filter: $filter) {
      edges {
        cursor
        node {
          id
          name
        }
      }
    }
  }

{
    person(id: $id) {
      id
      name
      user
      images {
        bin
        mimeType
      }
      primaryLocation {
        id
        name
      }
    }
  }

{
    person(id: $userId) {
      id
      name
      images {
        bin
        mimeType
        date
        description
        extension
        hash
        name
        size
      }
    }
  }

{
    economicResource(id: $id) {
      id
      name
      note
      metadata
      license
      repo
      classifiedAs
      accountingQuantity {
        hasNumericalValue
      }
      onhandQuantity {
        hasUnit {
          id
        }
        hasNumericalValue
      }
      conformsTo {
        id
        name
      }
      primaryAccountable {
        id
        name
        primaryLocation {
          name
          mappableAddress
          lat
          long
        }
      }
      currentLocation {
        id
        name
        mappableAddress
        lat
        long
      }
      images {
        hash
        name
        mimeType
        bin
        date
        description
        extension
        size
      }
    }
  }

{
    person(id: $id) {
      id
      name
      note
      email
      user
      images {
        hash
        name
        mimeType
        bin
        size
        extension
        description
      }
      ethereumAddress
      primaryLocation {
        id
        name
        mappableAddress
        lat
        long
      }
    }
  }

{
    economicResource(id: $id) {
      traceDpp
    }
  }

{
    personExists(email: $email, user: $user)
  }
