content:
{
    user {
      id
      graphs {
        id
        name
        stats(to: $to, from: $from, traceFilters: $traceFilters) {
          errorCount
          errorPercent
          count
          duration
        }
      }
    }
  }

{
    graphCreate(name: $name) {
      id
      name
      stats(to: $to, from: $from, traceFilters: $traceFilters) {
        errorCount
        errorPercent
        count
        duration
      }
    }
  }

{
    graph(graphId: $graphId) {
      id
      name
      stats(traceFilters: $traceFilters, from: $from, to: $to) {
        count
        duration
        errorCount
        errorPercent
      }
    }
  }

{
    graph(graphId: $graphId) {
      id
      name
      keys {
        id
        secret
        prefix
        createdAt
      }
    }
  }

{
    operation(
      graphId: $graphId
      operationId: $operationId
      traceFilters: $traceFilters
      to: $to
      from: $from
    ) {
      id
      key
      stats {
        count
        errorCount
        errorPercent
        duration
      }
    }
  }

{
    operations(
      graphId: $graphId
      orderBy: $orderBy
      after: $after
      traceFilters: $traceFilters
      to: $to
      from: $from
    ) {
      nodes {
        id
        key
        stats {
          count
          errorCount
          errorPercent
          duration
        }
      }
      cursor
    }
  }

{
    latencyDistribution(
      graphId: $graphId
      operationId: $operationId
      traceFilters: $traceFilters
      to: $to
      from: $from
    ) {
      nodes {
        duration
        count
      }
      cursor
    }
  }

{
    rpm(
      graphId: $graphId
      operationId: $operationId
      to: $to
      from: $from
      traceFilters: $traceFilters
    ) {
      nodes {
        startTime
        count
        errorCount
      }
      cursor
    }
  }

{
    trace(traceId: $traceId) {
      id
      key
      duration
      startTime
      endTime
      root
      details
    }
  }

{
    traces(
      graphId: $graphId
      operationId: $operationId
      after: $after
      orderBy: $orderBy
      traceFilters: $traceFilters
      to: $to
      from: $from
    ) {
      nodes {
        id
        duration
        startTime
        endTime
      }
      cursor
    }
  }

{
        result: registerMultiPayment(data: $data) { dv multiPaymentId webViewUrl feeCalculationUrl directPaymentUrl getCardTokensUrl }
    }

{
        result: registerMultiPaymentForOneReceipt(data: $data) { dv multiPaymentId webViewUrl feeCalculationUrl directPaymentUrl anonymousPaymentUrl }
    }

{
        result: registerMultiPaymentForVirtualReceipt(data: $data) { dv multiPaymentId webViewUrl feeCalculationUrl directPaymentUrl anonymousPaymentUrl }
    }

{
        result: ticketReportWidgetData(data: $data) { data { statusName, currentValue, growth, statusType } }
    }

{
        result: ticketAnalyticsReport(data: $data) { groups { count status property dayGroup categoryClassifier executor assignee } ticketLabels { color label } }
    }

{
        result: predictTransactionClassification(data: $data) { id name isOutcome category }
    }

name:
getOrganizationEmployeeByIdWithInviteCode (
        $id: ID!
    )

content:
{
        objs: allOrganizationEmployees(
            where: {
                id: $id
            }
            first: 1
        ) {
            id
            inviteCode
        }
    }

{
        obj: predictTicketClassification(data: $data) { id place { id name } category { id name }  }
    }

{
        result: signinResidentUser(data: $data) { user { id name }, token }
    }

{
            obj: authenticateUserWithPhoneAndPassword(data: { phone: $phone, password: $password }) {
                item {
                    id
                }
                token
            }
        }

{
        obj: predictTicketClassification(data: $data) { id place { id name } category { id name }  }
    }

{
        auth: authenticateUserWithPassword(email: $identity, password: $secret) {
            user: item {
                id
            }
            token
        }
    }

{
        obj: authenticateUserWithPhoneAndPassword(data: { phone: $phone, password: $password }) {
            item {
                id
            }
            token
        }
    }

{MODELS}($where: ${whereName}, $first: Int = ${DEFAULT_PAGE_SIZE}, $skip: Int, $sortBy: [${sortName}!]) {
             objs: ${queryName}(where: $where, first: $first, skip: $skip, sortBy: $sortBy) ${fields}
         }

{MODELS}($where: ${whereName}, $first: Int = ${DEFAULT_PAGE_SIZE}, $skip: Int, $sortBy: [${sortName}!]) {
             objs: ${queryName}(where: $where, first: $first, skip: $skip, sortBy: $sortBy) ${fields}
             meta: ${metaQueryName}(where: $where) { count }
         }

{MODELS}($where: ${MODEL}WhereInput, $first: Int = ${DEFAULT_PAGE_SIZE}, $skip: Int, $sortBy: [Sort${MODELS}By!]) {
            objs: all${MODELS}(where: $where, first: $first, skip: $skip, sortBy: $sortBy) ${fields}
        }

{MODELS}($where: ${MODEL}WhereInput, $first: Int = ${DEFAULT_PAGE_SIZE}, $skip: Int, $sortBy: [Sort${MODELS}By!]) {
            objs: all${MODELS}(where: $where, first: $first, skip: $skip, sortBy: $sortBy) ${fields}
            meta: _all${MODELS}Meta(where: $where) { count }
        }

{
        auth: authenticateUserWithPassword(email: $identity, password: $secret) {
            user: item {
                id
            }
        }
    }

{modelName}WebhookQuery($first: Int!, $skip: Int!, $where: ${modelName}WhereInput!) {
            objs: all${pluralName}(first: $first, skip: $skip, where: $where, sortBy: [updatedAt_ASC, id_ASC]) ${fields}
        }

{
        objs: allBillingProperties(where: $where, orderBy: $orderBy, first: $first, skip: $skip) {
            id
            address
        }
    }

{
        objs: allAcquiringIntegrationContexts(where: $where, orderBy: $orderBy, first: $first, skip: $skip) {
            id
            integration {
               id
               name
            }
        }
    }

name:
getMeter (
        $where: MeterWhereInput
    )

content:
{
        objs: allMeters(
            where: $where
        ) {
            id
            number
            unitName
            organization {
                id
            }
        }
    }

{
        objs: allMeterResources(
            where: {
                id: $id
            }
        ) {
            id
            number
            unitName
            organization {
                id
            }
        }
    }

{
        objs: allMeterResources(where: $where, orderBy: $orderBy, first: $first, skip: $skip) {
            id
            name
        }
    }

{
        objs: allProperties(where: $where, orderBy: $orderBy, first: $first, skip: $skip) {
            id
            address
            map { sections { floors { units { type label } } } parking { floors { units { type label } } } }
        }
    }

{
        objs: allPropertyScopes(where: $where, orderBy: $orderBy) {
            id
            name
            hasAllProperties
        }
    }

{
        objs: allPropertyScopeProperties(where: $where, first: $first) {
            id
            propertyScope { id }
            property { id }
        }
    }

{
        objs: allProperties(where: $where, orderBy: $orderBy, first: $first, skip: $skip) {
            id
            address
        }
    }

{
        objs: allProperties(where: {id: $propertyId, organization: { id: $organizationId }}) {
            id
            address
        }
    }

{
        objs: allTicketSources(where: {name_contains: $value, organization: { id: $organizationId }}) {
            id
            name
        }
    }

{
        objs: allTicketClassifiers(where: {name_contains_i: $value, organization_is_null: true, parent_is_null: true}) {
            id
            name
        }
    }

{
        objs: allProperties(where: $where, orderBy: $orderBy, first: $first, skip: $skip) {
            id
            address
        }
    }

{
        objs: allProperties(where: $where, orderBy: $orderBy, first: $first, skip: $skip) {
            id
            address
            map { sections { floors { units { label } } } }
        }
    }

{
        objs: allOrganizationEmployees(where: {name_contains_i: $value, organization: { id: $organizationId }}) {
            isBlocked
            name
            id
            user {
                id
            }
            role {
                id
                name
                canBeAssignedAsExecutor
                canBeAssignedAsResponsible
            }
        }
    }

{
        objs: allOrganizationEmployees(where: {name_contains_i: $value, organization: { id: $organizationId } }) {
            id
            name
            email
            user {
                id
            }
        }
    }

{
        objs: allContacts(where: {organization: { id: $organizationId }, property: { id: $propertyId }, unitName: $unitName, unitType: $unitType}) {
            id
            name
            phone
        }
    }

{
        all: _allTicketsMeta(where: $where) {
            count
        }
        ${TicketStatusTypeType.NewOrReopened}: _allTicketsMeta(where: { AND: [$whereWithoutStatuses, { status: { type: ${TicketStatusTypeType.NewOrReopened} } }]}) {
            count
        }
        ${TicketStatusTypeType.Processing}: _allTicketsMeta(where: { AND: [$whereWithoutStatuses, { status: { type: ${TicketStatusTypeType.Processing} } }]}) {
            count
        }
        ${TicketStatusTypeType.Deferred}: _allTicketsMeta(where: { AND: [$whereWithoutStatuses, { status: { type: ${TicketStatusTypeType.Deferred} } }]}) {
            count
        }
        ${TicketStatusTypeType.Canceled}: _allTicketsMeta(where: { AND: [$whereWithoutStatuses, { status: { type: ${TicketStatusTypeType.Canceled} } }]}) {
            count
        }
        ${TicketStatusTypeType.Completed}: _allTicketsMeta(where: { AND: [$whereWithoutStatuses, { status: { type: ${TicketStatusTypeType.Completed} } }]}) {
            count
        }
        ${TicketStatusTypeType.Closed}: _allTicketsMeta(where: { AND: [$whereWithoutStatuses, { status: { type: ${TicketStatusTypeType.Closed} } }]}) {
            count
        }
    }

{
        contacts: allContacts(
          where: {
            organization: { id: $organizationId },
            property_is_null: false,
            property: { deletedAt: null },
            phone_contains: $phone
          }, first: 10) {
            id
            name
            phone
            property { ${TICKET_PROPERTY_FIELDS} }
            unitName
            unitType
        }

        tickets: allTickets(
          where: $ticketsWhere, first: 10) {
          id
          property { ${TICKET_PROPERTY_FIELDS} }
          clientName
          clientPhone
          unitName
          unitType
          number
      }

        employees: allOrganizationEmployees(
          where: {
            organization: { id: $organizationId },
            phone_contains: $phone,
          }, first: 10) {
            id
            name
            phone
        }
    }

{
        objs: allOrganizationEmployees(first: $first, skip: $skip, where: $where) {
            name
            id
            user {
                id
            }
            role {
                id
                name
                canBeAssignedAsExecutor
                canBeAssignedAsResponsible
                ticketVisibilityType
            }
            hasAllSpecializations
            isBlocked
        }
    }

{
        objs: allOrganizationEmployeeSpecializations(first: $first, skip: $skip, where: {employee: { id_in: $employeeIds } }) {
            employee { id }
            specialization { id name }
        }
    }

