fragment commitFields on Commit {
  status {
    contexts {
      id
      context
      state
      createdAt
    }
    state
  }
  pushedDate
}

fragment statusFields on PullRequest {
	commits(last: 1) {
    nodes {
      commit {
        ...commitFields
      }
    }
  }
}

