  feed_comments(
    where: {
      feed_id: {_eq: $feed_id}}
  ) {
    feed_id
    comment_id
    posted_at
    approved
    approved_at
    approved_by
    poster {
      uid
      name
      username
      biography
      image
    }
    comment {
      id
      article_id
      thread_id
      parent_id
      created_at
      modified_at
      status
      text
      author {
        uid
        image
        username
        biography
      }
      recommends
      approved
      children_aggregate ( 
        where: {
          approved: {_eq: $approved}
        },
        order_by: {created_at: desc} 
      ) {
        aggregate { count }
      }
      children (
        where: {
          approved: {_eq: $approved}
        },
        order_by: {created_at: desc},
        limit: 1,
        offset: 0
      ) {
        id
        created_at
        recommends
        approved
        author {
          uid
          image
          username
          biography
        }
      }
    }
  }
}

{
  categories(
    where: {id: {_neq: "deleted"}},
    order_by: {name: asc}
  ) {
    id
    name
    description
    image
    coverImage
    emoji
    created_at
    created_by
    approved
    notifications
    updated_at
  }
}

