      price_candles(
        where: {
          base: "usd"
          period: $price_period
          timestamp_gt: $period_selected
        }
        orderBy: timestamp
        first: 365
      ) {
        timestamp
        close
      }
      # hourly TVL chart
      total_value_locked(
        where: { base: "usd", timestamp_gt: $period_selected }
        orderBy: timestamp
        first: 365
      ) {
        close
        timestamp
      }
      # hourly allocation chart
      weights(
        where: { timestamp_gt: $period_selected }
        orderBy: timestamp
        first: 365
      ) {
        timestamp
        weights {
          token {
            id
            symbol
          }
          weight_normalized
        }
      }
    }
  }

