{

    __schema {
      queryType { name }
      mutationType { name }
      types {
        ...FullType
      }
    }
  }

  fragment FullType on __Type {
    kind
    name
    description
    fields(includeDeprecated: true) {
      name
      description
      args {
        ...InputValue
      }
      type {
        ...TypeRef
      }
      isDeprecated
      deprecationReason
    }
    inputFields {
      ...InputValue
    }
    interfaces {
      ...TypeRef
    }
    enumValues(includeDeprecated: true) {
      name
      description
      isDeprecated
      deprecationReason
    }
    possibleTypes {
      ...TypeRef
    }
  }

  fragment InputValue on __InputValue {
    name
    description
    type { ...TypeRef }
    defaultValue
  }

  fragment TypeRef on __Type {
    kind
    name
    ofType {
      kind
      name
      ofType {
        kind
        name
        ofType {
          kind
          name
        }
      }
    }
  }

{
  __schema {
    queryType {
      fields {
        name
        args {
          name,
          type {
            name
            kind
            ofType {
              name
              kind
            }
          }
        }
        type {
          fields {
            name
            type {
              kind
              name
            }
          }
          kind
          ofType {
            fields {
              name
              description
              type {
                kind
                name
              }
            }
          }
        }
      }
    }
    mutationType {
      fields {
        name
        args {
          name,
          type {
            name
            kind
            ofType {
              name
              kind
            }
          }
        }
        type {
          fields {
            name
            type {
              kind
              name
            }
          }
          kind
          ofType {
            fields {
              name
              description
              type {
                kind
                name
              }
            }
          }
        }
      }
    }
  }
}

